mapper.update:
update user
set password = #{newPassword}
where id =#{userId}
and #{password} = #{password}

mapper.select:
SELECT DISTINCT u.id, u.name, u.sex, u.tel, u.email, d.dept_name AS dept, u.hiredate, u.root, u.status, ( SELECT GROUP_CONCAT( role_name separator "," ) FROM role WHERE JSON_CONTAINS ( u.role, CONVERT ( id, CHAR ) ) ) AS roles FROM user u JOIN role r ON JSON_CONTAINS ( u.role, CONVERT ( r.id, CHAR ) ) LEFT JOIN dept d ON u.dept_id = d.id WHERE 1=1
AND u.name LIKE "%${name}%"
AND u.sex=#{sex}
AND r.role_name=#{role}
AND d.id=#{deptId}
AND u.status=#{status}
LIMIT #{start}, #{length}

SELECT
COUNT(DISTINCT u.id)
FROM user u
JOIN role r ON JSON_CONTAINS ( u.role, CONVERT ( r.id, CHAR ) )
WHERE 1=1

    AND u.name LIKE "%${name}%"


    AND u.sex=#{sex}


    AND r.role_name=#{role}


    AND u.dept_id=#{deptId}


    AND u.status=#{status}
